    public static bool AreStringsAnagrams(string a, string b)
    {
        for (int i = 0; i < a.Length; i++)
        {
            int ind = b.IndexOf(a[i]);
            if (ind > -1)
                b = b.Remove(ind, 1);
            else
                return false;
        }

        if (b.Length > 0)
            return false;
        else
            return true;
    }

    public static int NumberOfWays(int n)
    {
        int jump = 2;
        int step = 1;
        
        int comb = 0;
        
        if (n % jump == 0)
            comb++;
        if (n % step == 0)
            comb++;
        if (n % (step + jump) == 0)
        {
            comb = comb + (int)Math.Exp(n / (step + jump));
        }

        return comb;
 
    }
